import React, { Component } from 'react';
import { connect } from 'react-redux'

import { reduxForm } from 'redux-form'
import { withRouter } from 'react-router-dom'

import * as actions from '../actions';

class Login extends Component {
    constructor(props) {
        super(props);

        //this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;

        this.props.auth['login'][name] = value
        Object.assign({}, this.props.auth)
    }

    handleSubmit(e) {
        e.preventDefault();

        this.props.userSubmited();
        const { auth:{ login }, history } = this.props;

        // stop here if form is invalid
        if (login == null || login == false || !(login.username && login.password)) {
            return;
        }

        this.props.userLoading();

        const {username, password } = login;

        this.props.login({username, password}, history);
    }

    render() {

        const { username, password, submitted, loading, error } = this.props.auth;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading}>Login</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        );
    }
}



export default reduxForm({form : 'login'})(connect(null, actions)(withRouter(Login)));